fn handle_char(q: &mut VecDeque<char>, c: char) -> Option<usize> {
  match c {
    '('|'{'|'['|'<' => q.push_front(c),
    _ => match (q.pop_front(), c) {
      (Some('('), ')') => {},
      (Some('['), ']') => {},
      (Some('{'), '}') => {},
      (Some('<'), '>') => {},
      (_, ')') => return Some(3),
      (_, ']') => return Some(57),
      (_, '}') => return Some(1197),
      (_, '>') => return Some(25137),
      _ => unreachable!(),
    },
  }
  None
}

fn score_line(s: &str) -> usize {
  let mut q = VecDeque::new();
  s.chars().find_map(|c| handle_char(&mut q, c)).unwrap_or(0)
}

fn complete(s: &str) -> Option<usize> {
  let mut q = VecDeque::new();
  let invalid = s.chars()
    .map(|c| handle_char(&mut q, c))
    .any(|o| o.is_some());
  if invalid {
    return None;
  }
  let score = q.iter().fold(0, |score,c| match c {
    '(' => score * 5 + 1,
    '[' => score * 5 + 2,
    '{' => score * 5 + 3,
    '<' => score * 5 + 4,
    _ => unreachable!()
  });
  Some(score)
}

aoc2021::main! {
  let p1 = INPUT.lines()
    .map(score_line)
    .sum::<usize>();
  let fixed_lines = INPUT.lines()
    .filter_map(complete)
    .sorted()
    .collect::<Vec<_>>();
  let p2 = fixed_lines[fixed_lines.len() / 2];
  (p1,p2)
}
